//./node_modules/Stack/Stack.js
class Stack
{
    constructor() {
        this.items = []; 
    }
    pop() {
        if (this.empty()) {
            console.log('Stack is empty'); 
        }
        else this.items.pop(); 
    }
    push(x) {
        this.items.push(x); 
    }
    empty() {
        return (this.items.length === 0) //neu rong thi tra ve true 
    }
    size() {
        return (this.items.length); 
    }
    top() {
        return (this.items[this.items.length - 1]); 
    }
}
module.exports = Stack; 